import { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

const API_URL = "https://6910cb5a7686c0e9c20bb7c5.mockapi.io/Tasks";


export default function CreatePage() {
  const nav = useNavigate();

  const nameRef = useRef();
  const emailRef = useRef();
  const ageRef = useRef();

  const [form, setForm] = useState({ name: "", email: "", age: "" });

  const handleChange = (e) => {
    setForm({...form, [e.target.name]: e.target.value });
  };

  const validate = () => {
    if (!form.name) { nameRef.current.focus(); return false; }
    if (!form.email) { emailRef.current.focus(); return false; }
    if (!form.age) { ageRef.current.focus(); return false; }
    return true;
  };

  const submit = () => {
    if (!validate()) return;

    fetch(API_URL, {
      method: "POST",
      headers: { "Content-Type": "application/json"},
      body: JSON.stringify(form)
    }).then(() => nav("/list"));
  };

  return (
    <div className="container mt-4">
      <h2>Create Page</h2>

      <input ref={nameRef} name="name" className="form-control mb-2" placeholder="name" onChange={handleChange}/>
      <input ref={emailRef} name="email" className="form-control mb-2" placeholder="email" onChange={handleChange}/>
      <input ref={ageRef} name="age" className="form-control mb-2" placeholder="age" onChange={handleChange}/>

      <button className="btn btn-primary" onClick={submit}>Submit</button>
    </div>
  );
}
