import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const API_URL = "https://6910cb5a7686c0e9c20bb7c5.mockapi.io/Tasks";


export default function ListPage() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch(API_URL)
      .then(res => res.json())
      .then(json => setData(json));
  }, []);

  const deleteItem = (id) => {
    fetch(`${API_URL}/${id}`, { method: "DELETE" })
      .then(() => setData(data.filter(item => item.id !== id)));
  };

  return (
    <div className="container mt-4">
      <h2>List Page</h2>
      <Link to="/create" className="btn btn-primary mb-3">Create</Link>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Email</th><th>Age</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map(d => (
            <tr key={d.id}>
              <td>{d.id}</td>
              <td>{d.name}</td>
              <td>{d.email}</td>
              <td>{d.age}</td>
              <td>
                <Link to={`/detail/${d.id}`} className="btn btn-sm btn-info me-1">Detail</Link>
                <Link to={`/update/${d.id}`} className="btn btn-sm btn-warning me-1">Update</Link>
                <button className="btn btn-sm btn-danger" onClick={() => deleteItem(d.id)}>Del</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
